      }


      getSingleBeer = () => {

        const { params } = this.props.match;

        axios
          .get(`https://ih-beers-api2.herokuapp.com/beers/${params.id}`)
          .then(responseFromApi => {
            const theBeer = responseFromApi.data;

            this.setState(theBeer);
          })
          .catch(err => {
            console.log(err);
          });
      };



    render() {
        return (
            <div>

                <h1>HOLA</h1>
