import React, { Component } from "react";
import { Link } from "react-router-dom";
import { withAuth } from "../lib/AuthProvider";
import axiosRequestFunctions from "../lib/auth-service";
import axios from "axios";


class Mygroups extends Component {
  state = { groups: [] };

componentDidMount() {
    this.getMyGroups();
}

  getMyGroups = async () => {
    const myGroups = await axios.get(`${process.env.REACT_APP_API_URI}/profile/${this.props.user._id}`)
    this.setState({groups: myGroups.data.groups});
    // const groupsList = this.state.groups.data.groups
    console.log("NUEVO CONSOLE LOG", myGroups.data.groups);
    // console.log("LA NUEVA LISTA DE GRUPOS", groupsList);
  }

  handleDelete = (idGroup) => { 
    console.log("ID GROUP", idGroup)
    axios.post(`${process.env.REACT_APP_API_URI}/groups/${idGroup}/delete`, {withCredentials: true})
    const filterPending = [...this.state.pending].filter((element)=> {
      return element._id !== idUser
    })
    console.log("Decline", filterPending)
    this.setState({
      pending: filterPending
    })
  }

  render() {
    return (
      <div className="groups">
          <h1>GROUPS</h1>
         {this.state.groups.map((group, index) => {
            return (
            <div key={index}>
                <Link to={`/groupdetails/${group._id}`}> <p>{group.name}</p> </Link>  <button onClick={ () => this.handleDelete()}>Abandonar grupo</button>
         </div>
         )}
         )}
         </div>
    );
  }
}

export default withAuth(Mygroups);
