import React, { Component } from "react";
import {Link} from "react-router-dom"
import axios from "axios";

class GroupDetails extends Component {
  constructor(props) {
    super(props);
    this.state = {
      group: [],
    };
  }
  componentDidMount() {
    this.getSingleGroup();
  }

  getSingleGroup = async () => {
    const { params } = this.props.match;
    //console.log(params);
    await axios
      .get(`${process.env.REACT_APP_API_URI}/group/${this.props.group._id}`
      )
      .then((responseFromApi) => {
        const theEvent = responseFromApi.data._embedded.events[0];

        this.setState({
          event: theEvent,
          image: theEvent.images[1].url,
        });

        //console.log("caca", this.state.event.images[0].url);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  render() {
    return (
      <div>
        <h1>Grupo</h1>
        <h6>{this.state.event.name}</h6>
        <h6>{this.state.event.type}</h6>

        <div className="flex-container">
          <figure className="image-container">
          <img src="https://images.unsplash.com/photo-1474600056930-615c3d706456?ixlib=rb-0.3.5&s=dc82336ad3e3873b0a81e9389d346916&auto=format&fit=crop&w=1952&q=80" className="image-prop" alt="" />
          </figure>
          <div className="image-prop" style={{backgroundColor: `#fff`}}>
            <div className="flex-container">
              <h1>Evento</h1>
              <button>#Vegetarian</button>
              <button>#Italian</button>
            </div>
            <h2>Yummy veggie pizza with tasty olives, crisp peppers, fresh arugula and original italian tomato sauce.</h2>
          </div>
          </div>
        <Link to={`/groups/create/${this.props.match.params.id}`}>
          <button>Create Group</button>
        </Link>
      </div>
    );
  }
}

export default GroupDetails;
