  constructor(props) {
    super(props);
    this.state = {
      group: [],
      creator: "",
      users: [],
      pending: [],
      isLoading: true,
    };
  }
  componentDidMount() {
    this.getSingleGroup();
  }

  getSingleGroup = () => {
    const { params } = this.props.match;
    axios
      .get(`${process.env.REACT_APP_API_URI}/groups/${params.id}`, {
        withCredentials: true,
      })
      .then((data) => {
        const theGroup = data.data;
        this.setState({
          group: theGroup,
          creator: theGroup.creator.username,
          users: theGroup.users,
          isLoading: false,
        });
