    constructor(props) {
        super(props);
        this.state = {};
      }
      componentDidMount() {
        this.getSingleEvent();
      }


      getSingleEvent = () => {

        const { params } = this.props.match;

        axios
          .get(`https://app.ticketmaster.com/discovery/v2/events?countryCode=es&apikey=QDt0kSZEGRnYXjA1swZt9oSdGKMnjQM1&id=${this.event.id}`)
          .then(responseFromApi => {
            const theEvent = responseFromApi.data;

            this.setState(theEvent);
          })
          .catch(err => {
            console.log(err);
          });
      };



    render() {
        return (
            <div>

                <h1>HOLA</h1>
                <h6>{this.state.name}</h6>


            </div>
        )
    }
}

export default EventDetails
